package com.example.imageapi.home_screens.presentation.screen

import android.graphics.BitmapFactory
import android.util.Base64
import androidx.compose.foundation.Image
import androidx.compose.foundation.layout.Column
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.foundation.layout.padding
import androidx.compose.material3.Button
import androidx.compose.material3.Scaffold
import androidx.compose.material3.Text
import androidx.compose.runtime.Composable
import androidx.compose.runtime.collectAsState
import androidx.compose.runtime.getValue
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.asImageBitmap
import androidx.compose.ui.layout.ContentScale
import androidx.lifecycle.viewmodel.compose.viewModel
import androidx.navigation.NavHostController
import com.example.imageapi.home_screens.presentation.CameraViewModel
import com.example.imageapi.navigation_setup.Screens
import java.io.ByteArrayInputStream

@Composable
fun FirstScreen(navHostController: NavHostController) {

    val viewModel = viewModel<CameraViewModel>()
    val bitmap by viewModel.bitmap.collectAsState()

    val base64String = ""
    val imageBitmap = base64ToImageBitmap(base64String)

    Scaffold (modifier = Modifier.fillMaxSize()){innerPadding->
        Column (modifier = Modifier.padding(innerPadding)){
            imageBitmap?.let {
                Image(it,"",contentScale = ContentScale.Fit)
            }
            Button(onClick = {
                navHostController.navigate(route = Screens.CameraScreen.route)
            }
            ) {
                Text("Capture Image!")
            }
            bitmap?.let {
                Image(bitmap = it.asImageBitmap(), "")
            }
        }
    }
}

fun base64ToImageBitmap(base64String: String): androidx.compose.ui.graphics.ImageBitmap? {
    return try {
        val decodedBytes = Base64.decode(base64String, Base64.DEFAULT)
        val bitmap = BitmapFactory.decodeStream(ByteArrayInputStream(decodedBytes))
        bitmap?.asImageBitmap()
    } catch (e: Exception) {
        e.printStackTrace()
        null
    }
}